mapper.select:
select id, NAME, level_name, level_num, phone, sex, balance_amount, balance_giftmoney_amount, consume_count, balance_integral, FROM_UNIXTIME(last_consume_time) as last_consume_time, store_id from member_error_ht where store_id = #{params.storeId}
and ( name LIKE CONCAT('%',#{params.content},'%') or phone LIKE CONCAT('%',#{params.content},'%') or level_num LIKE CONCAT('%',#{params.content},'%') )
and is_deleted = 0
